pragma solidity ^0.5.0;

contract Bank {

    mapping(address => uint256) public accounts;

    function deposit(uint256 amount) public payable returns (uint256) {
        require(msg.sender.balance == amount);
        accounts[msg.sender] += amount;
        return amount;
    } 

    function withdraw(uint256 amount) public returns (uint256) {
        require(amount <= accounts[msg.sender]);
        address payable payableMsgSender = address(uint160(address(msg.sender)));
        accounts[msg.sender] -= amount;
        payableMsgSender.transfer(amount);
        return amount;
    }

    function getBalance() public view returns (uint256) {
        return accounts[msg.sender];
    }

}
